; $x < $num; $x++)				{					$output = $output . "o ";				}				return $output . "</div><br>";			}				$passenger = $_POST["passenger"];				$bus = floor($passenger / 30);				$car = floor(($passenger % 30) / 4);				$remainder = ($passenger % 30) %4;				echo  $passenger . " passengers need transportation: <br><br>";				echo  $bus . " Buses Needed". printo($bus);				echo $car . " Cars Needed". printo($car);				echo $remainder . " Passengers left over". printo($remainder);			?>			</div>				<div class = "outer">				<div class = "calculate">				Legend: Passenger per Bus = 30 | per Car = 4				</div>			</div>			<?phpdate_default_timezone_set('EST');echo "<br>Last Modified: " . date ("H:i:s F d Y ", getlastmod()) . "EST | "?><a href = "index.html">Return to form page</a></div><div class="copyright">there is nothing that requires a copyright notice</div></body></html>